import React, { useState, useEffect } from 'react';
import api from '../api'; // Import the Axios API instance

function TaskList() {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks when component mounts
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await api.get('/tasks'); // Endpoint to fetch tasks
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  // Handle task deletion
  const deleteTask = async (id) => {
    try {
      await api.delete(`/tasks/${id}`); // Endpoint to delete a task
      setTasks(tasks.filter((task) => task._id !== id)); // Remove the deleted task from the state
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  // Function to sort tasks by priority
  const sortTasksByPriority = () => {
    const sortedTasks = [...tasks].sort((a, b) => {
      const priorityOrder = { low: 1, medium: 2, high: 3 };
      return priorityOrder[a.priority] - priorityOrder[b.priority];
    });
    setTasks(sortedTasks);
  };

  // Group tasks by category
  const groupedTasks = tasks.reduce((acc, task) => {
    if (!acc[task.category]) {
      acc[task.category] = [];
    }
    acc[task.category].push(task);
    return acc;
  }, {});

  return (
    <div>
      <h3>Task List</h3>
      <button onClick={sortTasksByPriority}>Sort by Priority</button>
      {Object.keys(groupedTasks).map((category) => (
        <div key={category}>
          <h4>{category}</h4>
          <table border="1" style={{ width: '100%', marginBottom: '20px' }}>
            <thead>
              <tr>
                <th>Task Title</th>
                <th>Due Date</th>
                <th>Priority</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {groupedTasks[category].map((task) => {
                const formattedDate = new Date(task.dueDate).toLocaleString();
                return (
                  <tr key={task._id}>
                    <td>{task.title}</td>
                    <td>{formattedDate}</td>
                    <td>{task.priority}</td>
                    <td>
                      <button onClick={() => deleteTask(task._id)}>Delete</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default TaskList;
