import React, { useState, useEffect } from 'react';
import api from '../api'; // Import the Axios API instance

function TaskList() {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks when component mounts
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await api.get('/tasks'); // Endpoint to fetch tasks
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  // Handle task deletion
  const deleteTask = async (id) => {
    try {
      await api.delete(`/tasks/${id}`); // Endpoint to delete a task
      setTasks(tasks.filter((task) => task._id !== id)); // Remove the deleted task from the state
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Task List</h3>
      <ul>
        {tasks.length === 0 ? (
          <li>No tasks available</li>
        ) : (
          tasks.map((task) => {
            const formattedDate = new Date(task.dueDate).toLocaleString();
            return (
              <li key={task._id}>
                <strong>{task.title}</strong> - {formattedDate} - {task.priority}
                <button onClick={() => deleteTask(task._id)}>Delete</button>
              </li>
            );
          })
        )}
      </ul>
    </div>
  );
}

export default TaskList;
