import React from 'react';

function TaskList({ tasks }) {
  return (
    <div>
      <h3>Task List</h3>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ padding: '8px', textAlign: 'left', border: '1px solid #ddd' }}>Title</th>
            <th style={{ padding: '8px', textAlign: 'left', border: '1px solid #ddd' }}>Due Date</th>
            <th style={{ padding: '8px', textAlign: 'left', border: '1px solid #ddd' }}>Priority</th>
            <th style={{ padding: '8px', textAlign: 'left', border: '1px solid #ddd' }}>Category</th>
            <th style={{ padding: '8px', textAlign: 'left', border: '1px solid #ddd' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr>
              <td colSpan="5" style={{ padding: '10px', textAlign: 'center' }}>No tasks available</td>
            </tr>
          ) : (
            tasks.map((task) => {
              const formattedDate = new Date(task.dueDate).toLocaleString();
              return (
                <tr key={task._id}>
                  <td style={{ padding: '8px', border: '1px solid #ddd' }}>{task.title}</td>
                  <td style={{ padding: '8px', border: '1px solid #ddd' }}>{formattedDate}</td>
                  <td style={{ padding: '8px', border: '1px solid #ddd' }}>{task.priority}</td>
                  <td style={{ padding: '8px', border: '1px solid #ddd' }}>{task.category}</td>
                  <td style={{ padding: '8px', border: '1px solid #ddd' }}>
                    <button
                      onClick={() => deleteTask(task._id)}
                      style={{
                        padding: '5px 10px',
                        backgroundColor: '#dc3545',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                        borderRadius: '4px',
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
}

export default TaskList;
