import React from 'react';

function TaskList({ tasks }) {
  return (
    <div>
      <h3>Task List</h3>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Due Date</th>
            <th>Priority</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr>
              <td colSpan="5">No tasks available</td>
            </tr>
          ) : (
            tasks.map((task) => {
              const formattedDate = new Date(task.dueDate).toLocaleString();
              return (
                <tr key={task._id}>
                  <td>{task.title}</td>
                  <td>{formattedDate}</td>
                  <td>{task.priority}</td>
                  <td>{task.category}</td>
                  <td>
                    <button onClick={() => deleteTask(task._id)}>Delete</button>
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
}

export default TaskList;
